import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  Send, 
  Paperclip, 
  Camera, 
  Phone, 
  Video,
  MoreVertical,
  Check,
  CheckCheck,
  Clock
} from 'lucide-react';

/**
 * 聊天页面组件
 * 提供用户间的即时通讯功能，支持文本、图片、文件等消息类型
 */
const ChatPage: React.FC = () => {
  const { orderId } = useParams<{ orderId: string }>();
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'user',
      content: '您好，我想租这台相机，请问明天可以取吗？',
      timestamp: new Date(Date.now() - 3600000),
      read: true,
      type: 'text'
    },
    {
      id: 2,
      sender: 'other',
      content: '可以的，明天上午10点后您就可以来取。需要我提前准备好哪些配件吗？',
      timestamp: new Date(Date.now() - 1800000),
      read: true,
      type: 'text'
    },
    {
      id: 3,
      sender: 'user',
      content: '太好了！我需要三个电池和一个128GB的存储卡，谢谢！',
      timestamp: new Date(Date.now() - 600000),
      read: false,
      type: 'text'
    }
  ]);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // 模拟对方信息
  const otherUser = {
    name: '摄影师小王',
    avatar: '/api/placeholder/50/50',
    online: true,
    lastSeen: new Date()
  };

  // 滚动到底部
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // 发送消息
  const handleSendMessage = () => {
    if (message.trim()) {
      const newMessage = {
        id: messages.length + 1,
        sender: 'user' as const,
        content: message,
        timestamp: new Date(),
        read: false,
        type: 'text' as const
      };
      setMessages([...messages, newMessage]);
      setMessage('');
    }
  };

  // 格式化时间
  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('zh-CN', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  // 格式化日期
  const formatDate = (date: Date) => {
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return '今天';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return '昨天';
    } else {
      return date.toLocaleDateString('zh-CN', { month: 'short', day: 'numeric' });
    }
  };

  // 按日期分组消息
  const groupMessagesByDate = () => {
    const grouped: { [key: string]: typeof messages } = {};
    messages.forEach(msg => {
      const dateKey = formatDate(msg.timestamp);
      if (!grouped[dateKey]) {
        grouped[dateKey] = [];
      }
      grouped[dateKey].push(msg);
    });
    return grouped;
  };

  const groupedMessages = groupMessagesByDate();

  return (
    <div className="min-h-screen bg-neutral-50"
    >
      <div className="max-w-4xl mx-auto bg-white h-screen flex flex-col"
    >
        {/* 头部 */}
        <div className="bg-white border-b px-4 py-3 flex items-center justify-between"
        >
          <div className="flex items-center space-x-3"
          >
            <img
              src={otherUser.avatar}
              alt={otherUser.name}
              className="w-10 h-10 rounded-full"
            />
            <div>
              <h3 className="font-semibold text-neutral-800">{otherUser.name}</h3>
              <p className="text-sm text-neutral-500"
              >
                {otherUser.online ? '在线' : `最后活跃 ${otherUser.lastSeen.toLocaleTimeString('zh-CN')}`}
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-2"
          >
            <button className="p-2 hover:bg-neutral-100 rounded-full transition-colors"
            >
              <Phone className="w-5 h-5 text-neutral-600" />
            </button>
            <button className="p-2 hover:bg-neutral-100 rounded-full transition-colors"
            >
              <Video className="w-5 h-5 text-neutral-600" />
            </button>
            <button className="p-2 hover:bg-neutral-100 rounded-full transition-colors"
            >
              <MoreVertical className="w-5 h-5 text-neutral-600" />
            </button>
          </div>
        </div>

        {/* 消息列表 */}
        <div className="flex-1 overflow-y-auto p-4"
        >
          {Object.entries(groupedMessages).map(([date, dateMessages]) => (
            <div key={date}
            >
              <div className="text-center my-4"
              >
                <span className="bg-neutral-100 text-neutral-600 text-xs px-3 py-1 rounded-full"
                >{date}</span>
              </div>

              <div className="space-y-4"
              >
                {dateMessages.map((msg) => (
                  <motion.div
                    key={msg.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`max-w-xs lg:max-w-md`}
                    >
                      <div className={cn(
                        "rounded-2xl px-4 py-2",
                        msg.sender === 'user' 
                          ? "bg-primary-500 text-white" 
                          : "bg-neutral-100 text-neutral-800"
                      )}
                      >
                        <p className="text-sm">{msg.content}</p>
                      </div>

                      <div className="flex items-center justify-end mt-1 space-x-1"
                      >
                        <span className="text-xs text-neutral-500"
                        >{formatTime(msg.timestamp)}</span>
                        {msg.sender === 'user' && (
                          <>
                            {msg.read ? (
                              <CheckCheck className="w-3 h-3 text-blue-500" />
                            ) : (
                              <Check className="w-3 h-3 text-neutral-400" />
                            )}
                          </>
                        )}
                      </div>
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>
          ))}

          <div ref={messagesEndRef} />
        </div>

        {/* 输入区域 */}
        <div className="border-t bg-white px-4 py-3"
        >
          <div className="flex items-center space-x-3"
          >
            <button className="p-2 hover:bg-neutral-100 rounded-full transition-colors"
            >
              <Paperclip className="w-5 h-5 text-neutral-600" />
            </button>

            <button className="p-2 hover:bg-neutral-100 rounded-full transition-colors"
            >
              <Camera className="w-5 h-5 text-neutral-600" />
            </button>

            <div className="flex-1"
            >
              <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                placeholder="输入消息..."
                className="w-full px-4 py-2 border border-neutral-300 rounded-full focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>

            <button
              onClick={handleSendMessage}
              disabled={!message.trim()}
              className={cn(
                "p-2 rounded-full transition-colors",
                message.trim()
                  ? "bg-primary-500 text-white hover:bg-primary-600"
                  : "bg-neutral-100 text-neutral-400 cursor-not-allowed"
              )}
            >
              <Send className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
