import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { AuthProvider } from './hooks/useAuth';
import { ChatProvider } from './hooks/useChat';

// 页面组件
import LandingPage from './pages/LandingPage';
import ResourceListingPage from './pages/ResourceListingPage';
import ResourceDetailPage from './pages/ResourceDetailPage';
import UserProfilePage from './pages/UserProfilePage';
import AuthPage from './pages/AuthPage';
import BookingPage from './pages/BookingPage';
import ChatPage from './pages/ChatPage';
import TestSimple from './pages/TestSimple';

// 组件
import Header from './components/Header';
import Footer from './components/Footer';

// 样式
import './index.css';

// 创建 React Query 客户端
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 3,
      staleTime: 5 * 60 * 1000, // 5分钟
      gcTime: 10 * 60 * 1000, // 10分钟 (新版本中cacheTime改为gcTime)
    },
  },
});

/**
 * 应用主组件
 * 提供路由配置和全局状态管理
 */
function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <ChatProvider>
          <Router>
          <div className="min-h-screen bg-neutral-50">
            {/* 头部导航 */}
            <Header />

            {/* 主内容区域 */}
            <main className="flex-1">
              <Routes>
                {/* 测试页面 */}
                <Route path="/" element={<TestSimple />} />

                {/* 首页 */}
                <Route path="/landing" element={<LandingPage />} />

                {/* 资源列表页 */}
                <Route path="/resources" element={<ResourceListingPage />} />

                {/* 资源详情页 */}
                <Route path="/resources/:id" element={<ResourceDetailPage />} />

                {/* 用户资料页 */}
                <Route path="/profile" element={<UserProfilePage />} />

                {/* 认证页面 */}
                <Route path="/auth" element={<AuthPage />} />

                {/* 预订页面 */}
                <Route path="/booking/:resourceId" element={<BookingPage />} />

                {/* 聊天页面 */}
                <Route path="/chat/:orderId?" element={<ChatPage />} />
              </Routes>
            </main>

            {/* 底部 */}
            <Footer />

            {/* 全局通知组件 */}
            <Toaster
              position="top-right"
              toastOptions={{
                duration: 4000,
                style: {
                  background: '#363636',
                  color: '#fff',
                  borderRadius: '8px',
                },
                success: {
                  iconTheme: {
                    primary: '#22c55e',
                    secondary: '#fff',
                  },
                },
                error: {
                  iconTheme: {
                    primary: '#ef4444',
                    secondary: '#fff',
                  },
                },
              }}
            />
          </div>
          </Router>
        </ChatProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;
