import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Search, 
  MapPin, 
  Star, 
  Users, 
  Shield, 
  Heart,
  TrendingUp,
  Gift,
  Clock,
  Award,
  ArrowRight
} from 'lucide-react';
import { cn } from '../utils/cn';

/**
 * 首页着陆页面组件
 * 展示平台特色、热门资源、统计数据和用户评价
 */
const LandingPage: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [currentCategory, setCurrentCategory] = useState(0);
  const [isVisible, setIsVisible] = useState(false);

  // 页面加载动画
  useEffect(() => {
    setIsVisible(true);
  }, []);

  // 热门分类数据
  const categories = [
    { 
      name: '电子设备', 
      icon: '📱', 
      count: 1250,
      color: 'from-blue-500 to-blue-600',
      description: '手机、电脑、相机等'
    },
    { 
      name: '家具家居', 
      icon: '🏠', 
      count: 890,
      color: 'from-green-500 to-green-600',
      description: '桌椅、沙发、床品等'
    },
    { 
      name: '工具设备', 
      icon: '🔧', 
      count: 567,
      color: 'from-orange-500 to-orange-600',
      description: '电动工具、维修设备等'
    },
    { 
      name: '交通工具', 
      icon: '🚗', 
      count: 234,
      color: 'from-purple-500 to-purple-600',
      description: '自行车、电动车等'
    },
    { 
      name: '服装配饰', 
      icon: '👗', 
      count: 1890,
      color: 'from-pink-500 to-pink-600',
      description: '礼服、首饰、包包等'
    },
    { 
      name: '运动户外', 
      icon: '⚽', 
      count: 445,
      color: 'from-red-500 to-red-600',
      description: '运动器材、露营装备等'
    }
  ];

  // 热门资源数据
  const featuredResources = [
    {
      id: 1,
      title: 'iPhone 15 Pro Max',
      description: '全新未拆封，256GB，支持5G网络',
      price: 45,
      unit: '天',
      image: '/api/placeholder/300/200',
      rating: 4.9,
      reviews: 127,
      location: '北京市朝阳区',
      owner: '张小明',
      verified: true,
      tags: ['全新', '正品', '包邮']
    },
    {
      id: 2,
      title: '戴森吸尘器 V15',
      description: '家用无线吸尘器，超强吸力，适合各种地面',
      price: 35,
      unit: '天',
      image: '/api/placeholder/300/200',
      rating: 4.8,
      reviews: 89,
      location: '上海市浦东新区',
      owner: '李美丽',
      verified: true,
      tags: ['九成新', '专业级', '免费送货']
    },
    {
      id: 3,
      title: '佳能 EOS R6',
      description: '专业级全画幅微单相机，适合摄影爱好者',
      price: 120,
      unit: '天',
      image: '/api/placeholder/300/200',
      rating: 5.0,
      reviews: 45,
      location: '深圳市南山区',
      owner: '王摄影',
      verified: true,
      tags: ['专业级', '全套配件', '技术指导']
    }
  ];

  // 平台统计数据
  const stats = [
    { number: '50万+', label: '注册用户', icon: Users, color: 'text-primary-500' },
    { number: '100万+', label: '闲置物品', icon: Gift, color: 'text-secondary-500' },
    { number: '98%', label: '用户满意度', icon: Star, color: 'text-yellow-500' },
    { number: '24/7', label: '客服支持', icon: Clock, color: 'text-green-500' }
  ];

  // 用户评价数据
  const testimonials = [
    {
      name: '陈小明',
      avatar: '/api/placeholder/64/64',
      rating: 5,
      comment: '第一次使用闲置租赁平台，体验非常好！租到了心仪的相机，价格也很实惠。',
      role: '摄影爱好者',
      location: '北京'
    },
    {
      name: '李美丽',
      avatar: '/api/placeholder/64/64',
      rating: 5,
      comment: '把我的闲置吸尘器租出去，不仅赚了钱还认识了新朋友，一举两得！',
      role: '全职妈妈',
      location: '上海'
    },
    {
      name: '王大锤',
      avatar: '/api/placeholder/64/64',
      rating: 5,
      comment: '平台的安全保障做得很好，实名认证和押金机制让人很放心。',
      role: '工程师',
      location: '深圳'
    }
  ];

  return (
    <div className="min-h-screen bg-neutral-50">
      {/* 英雄区域 */}
      <section className="relative bg-gradient-to-br from-primary-50 via-white to-secondary-50 py-20 lg:py-32">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            {/* 左侧内容 */}
            <div className="space-y-8">
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: isVisible ? 1 : 0, y: isVisible ? 0 : 30 }}
                transition={{ duration: 0.8, delay: 0.2 }}
                className="space-y-4"
              >
                <h1 className="text-4xl lg:text-6xl font-bold leading-tight">
                  <span className="text-gradient bg-gradient-to-r from-primary-600 to-secondary-600 bg-clip-text text-transparent">
                    让闲置变现金
                  </span>
                  <br />
                  <span className="text-neutral-800">
                    让资源流动起来
                  </span>
                </h1>
                <p className="text-lg lg:text-xl text-neutral-600 leading-relaxed max-w-lg">
                  将您的闲置物品租给需要的人，既能赚取额外收入，又能帮助他人。
                  安全、便捷、值得信赖的闲置资源租赁平台。
                </p>
              </motion.div>

              {/* 搜索框 */}
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: isVisible ? 1 : 0, y: isVisible ? 0 : 20 }}
                transition={{ duration: 0.8, delay: 0.4 }}
                className="max-w-lg"
              >
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                    <Search className="h-5 w-5 text-neutral-400" />
                  </div>
                  <input
                    type="text"
                    placeholder="搜索您需要的物品..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full pl-12 pr-32 py-4 border border-neutral-200 rounded-full text-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent shadow-lg"
                    onKeyPress={(e) => {
                      if (e.key === 'Enter' && searchQuery.trim()) {
                        window.location.href = `/resources?search=${encodeURIComponent(searchQuery)}`;
                      }
                    }}
                  />
                  <Link
