import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Mail, 
  Lock, 
  User, 
  Phone, 
  Eye, 
  EyeOff,
  Check,
  AlertCircle
} from 'lucide-react';
import { cn } from '../utils/cn';

/**
 * 认证页面组件
 * 提供用户注册和登录功能，包含表单验证和动画效果
 */
const AuthPage: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'login' | 'register'>('login');
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    username: '',
    phone: '',
    confirmPassword: '',
    agreeTerms: false
  });
  const [errors, setErrors] = useState<Record<string, string>>({});

  // 表单验证
  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (activeTab === 'register') {
      if (!formData.username.trim()) {
        newErrors.username = '请输入用户名';
      }
      if (!formData.phone.trim()) {
        newErrors.phone = '请输入手机号';
      } else if (!/^1[3-9]\d{9}$/.test(formData.phone)) {
        newErrors.phone = '请输入正确的手机号';
      }
      if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = '两次输入的密码不一致';
      }
      if (!formData.agreeTerms) {
        newErrors.agreeTerms = '请同意服务条款';
      }
    }

    if (!formData.email.trim()) {
      newErrors.email = '请输入邮箱地址';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = '请输入正确的邮箱地址';
    }

    if (!formData.password) {
      newErrors.password = '请输入密码';
    } else if (formData.password.length < 6) {
      newErrors.password = '密码长度至少6位';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // 处理表单提交
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      // 这里应该调用API进行注册或登录
      console.log('提交表单:', formData);
    }
  };

  // 处理输入变化
  const handleInputChange = (field: string, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  // 表单字段配置
  const loginFields = [
    {
      name: 'email',
      type: 'email',
      placeholder: '请输入邮箱地址',
      icon: Mail,
      label: '邮箱地址'
    },
    {
      name: 'password',
      type: 'password',
      placeholder: '请输入密码',
      icon: Lock,
      label: '密码'
    }
  ];

  const registerFields = [
    {
      name: 'username',
      type: 'text',
      placeholder: '请输入用户名',
      icon: User,
      label: '用户名'
    },
    {
      name: 'email',
      type: 'email',
      placeholder: '请输入邮箱地址',
      icon: Mail,
      label: '邮箱地址'
    },
    {
      name: 'phone',
      type: 'tel',
      placeholder: '请输入手机号',
      icon: Phone,
      label: '手机号'
    },
    {
      name: 'password',
      type: 'password',
      placeholder: '请输入密码（至少6位）',
      icon: Lock,
      label: '密码'
    },
    {
      name: 'confirmPassword',
      type: 'password',
      placeholder: '请再次输入密码',
      icon: Lock,
      label: '确认密码'
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 via-white to-secondary-50 flex items-center justify-center py-12 px-4"
    >
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-md w-full"
      >
        <div className="bg-white rounded-2xl shadow-xl p-8"
        >
          {/* Logo 和标题 */}
          <div className="text-center mb-8"
          >
            <div className="w-16 h-16 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center mx-auto mb-4"
            >
              <span className="text-white font-bold text-xl">租</span>
            </div>
            <h1 className="text-2xl font-bold text-neutral-800 mb-2"
            >
              闲置租赁平台
            </h1>
            <p className="text-neutral-600"
            >
              {activeTab === 'login' ? '欢迎回来，请登录您的账户' : '创建新账户，开始租赁之旅'}
            </p>
          </div>

          {/* Tab 切换 */}
          <div className="flex bg-neutral-100 rounded-lg p-1 mb-6"
          >
            <button
              onClick={() => setActiveTab('login')}
              className={cn(
                "flex-1 py-2 rounded-md font-medium transition-colors",
                activeTab === 'login'
                  ? 'bg-white text-primary-600 shadow-sm'
                  : 'text-neutral-600 hover:text-neutral-800'
              )}
            >
              登录
            </button>
            <button
              onClick={() => setActiveTab('register')}
              className={cn(
                "flex-1 py-2 rounded-md font-medium transition-colors",
                activeTab === 'register'
                  ? 'bg-white text-primary-600 shadow-sm'
                  : 'text-neutral-600 hover:text-neutral-800'
              )}
            >
              注册
            </button>
          </div>

          {/* 表单 */}
          <form onSubmit={handleSubmit} className="space-y-4"
          >
            <AnimatePresence mode="wait"
            >
              <motion.div
                key={activeTab}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
                className="space-y-4"
              >
                {/* 动态表单字段 */}
                {(activeTab === 'login' ? loginFields : registerFields).map((field, index) => (
                  <motion.div
                    key={field.name}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <label className="block text-sm font-medium text-neutral-700 mb-1"
                    >
                      {field.label}
                    </label>
                    <div className="relative"
                    >
                      <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none"
                      >
                        <field.icon className="h-5 w-5 text-neutral-400" />
                      </div>
                      <input
                        type={field.name === 'password' || field.name === 'confirmPassword' ? 
                          (showPassword ? 'text' : 'password') : field.type}
                        placeholder={field.placeholder}
                        value={formData[field.name as keyof typeof formData] as string}
                        onChange={(e) => handleInputChange(field.name, e.target.value)}
                        className={cn(
                          "block w-full pl-10 pr-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500",
                          errors[field.name] ? "border-red-500" : "border-neutral-300"
                        )}
                      />
                      {(field.name === 'password' || field.name === 'confirmPassword') && (
                        <button
                          type="button"
                          onClick={() => setShowPassword(!showPassword)}
                          className="absolute inset-y-0 right-0 pr-3 flex items-center"
                        >
                          {showPassword ? (
                            <EyeOff className="h-5 w-5 text-neutral-400" />
                          ) : (
                            <Eye className="h-5 w-5 text-neutral-400" />
                          )}
                        </button>
                      )}
                    </div>
                    {errors[field.name] && (
                      <p className="mt-1 text-sm text-red-600 flex items-center"
                      >
                        <AlertCircle className="w-4 h-4 mr-1" />
                        {errors[field.name]}
                      </p>
                    )}
                  </motion.div>
                ))}

                {/* 注册时的服务条款 */}
                {activeTab === 'register' && (
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.5 }}
                    className="flex items-start"
                  >
                    <input
                      type="checkbox"
                      id="agreeTerms"
                      checked={formData.agreeTerms}
                      onChange={(e) => handleInputChange('agreeTerms', e.target.checked)}
                      className="mt-1 w-4 h-4 text-primary-600 rounded border-neutral-300 focus:ring-primary-500"
                    />
                    <label htmlFor="agreeTerms" className="ml-2 text-sm text-neutral-600"
                    >
                      我已阅读并同意 <a href="#" className="text-primary-600 hover:text-primary-700">服务条款</a> 和 <a href="#" className="text-primary-600 hover:text-primary-700">隐私政策</a>
                    </label>
                  </motion.div>
                )}

                {/* 忘记密码 */}
                {activeTab === 'login' && (
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.3 }}
                    className="text-right"
                  >
                    <a href="#" className="text-sm text-primary-600 hover:text-primary-700"
                    >
                      忘记密码？
                    </a>
                  </motion.div>
                )}

                {/* 提交按钮 */}
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  type="submit"
                  className="w-full bg-gradient-to-r from-primary-500 to-secondary-500 text-white py-3 rounded-lg font-medium hover:from-primary-600 hover:to-secondary-600 transition-all duration-300 shadow-lg"
                >
                  {activeTab === 'login' ? '登录' : '注册'}
                </motion.button>
              </motion.div>
            </AnimatePresence>
          </form>

          {/* 社交登录 */}
          <div className="mt-6"
          >
            <div className="relative"
            >
              <div className="absolute inset-0 flex items-center"
              >
                <div className="w-full border-t border-neutral-200"></div>
              </div>
              <div className="relative flex justify-center text-sm"
              >
                <span className="px-2 bg-white text-neutral-500">或</span>
              </div>
            </div>

            <div className="mt-6 grid grid-cols-2 gap-3"
            >
              <button className="w-full inline-flex justify-center items-center px-4 py-2 border border-neutral-300 rounded-lg shadow-sm bg-white text-sm font-medium text-neutral-700 hover:bg-neutral-50"
              >
                <img className="w-5 h-5 mr-2" src="https://www.google.com/favicon.ico" alt="Google" />
                Google
              </button>
              <button className="w-full inline-flex justify-center items-center px-4 py-2 border border-neutral-300 rounded-lg shadow-sm bg-white text-sm font-medium text-neutral-700 hover:bg-neutral-50"
              >
                <img className="w-5 h-5 mr-2" src="https://weixin.qq.com/favicon.ico" alt="WeChat" />
                微信
              </button>
            </div>
          </div>

          {/* 底部提示 */}
          <p className="mt-6 text-center text-sm text-neutral-600"
          >
            {activeTab === 'login' ? (
              <>
                还没有账户？ <button
                  type="button"
                  onClick={() => setActiveTab('register')}
                  className="text-primary-600 hover:text-primary-700 font-medium"
                >
                  立即注册
                </button>
              </>
            ) : (
              <>
                已有账户？ <button
                  type="button"
                  onClick={() => setActiveTab('login')}
                  className="text-primary-600 hover:text-primary-700 font-medium"
                >
                  立即登录
                </button>
              </>
            )}
          </p>
        </div>
      </motion.div>
    </div>
  );
};

export default AuthPage;
